import React, {useEffect, useState} from 'react';
import './App.css';

const App = () => {


  const topic = "huh";
  const limit = 100;
  const BASE_API_URL = `http://export.arxiv.org/api/query?search_query=all:"${topic}"&sortBy=submittedDate&sortOrder=descending&max_results=${limit}`;


  const [counter, setCounter] = useState(0);

  useEffect(() => {
    scrapeArticles();
  }, []);

  const scrapeArticles = async () => {
    const response = await fetch(BASE_API_URL);
    const responseString = await response.text();
    const data = (new window.DOMParser()).parseFromString(responseString, "text/xml")
    const entries = data.querySelectorAll('entry');
    console.log("Scraped " + entries.length);

    for (var i=0; i < entries.length; i++) {
      console.log(entries[i].querySelector('title').textContent);
      console.log(entries[i].querySelector('published').textContent);
      console.log(entries[i].querySelector('updated').textContent);
      const authors = entries[i].querySelectorAll('author'); 
      for (var j=0; j < authors.length; j++) {
        console.log(authors[j].textContent);
      }
      console.log(entries[i].querySelector('summary').textContent);
    }
  }


  return (
    <div className='App'>
      <form className="input-form">
        <input className="topic-search-bar" type="text"/>
        <button className="submit-button" type="submit">
          Scrape arXiv
        </button>
      </form>
    </div>
  );
};

export default App;
