import React, { useEffect, useState } from 'react';
import { Spinner } from 'react-bootstrap';
import { Helmet } from 'react-helmet';
import {CSVLink} from 'react-csv';
import Entry from './Entry.js';
import './App.css';

const App = () => {

  const [entries, setEntries] = useState([]);
  const [topicInput, setTopicInput] = useState('');
  const [limitInput, setLimitInput] = useState('');
  const [startDateInput, setStartDateInput] = useState('');
  const [endDateInput, setEndDateInput] = useState('');
  const [showResults, setShowResults] = useState(true);

  const [query, setQuery] = useState({
    topic: topicInput,
    limit: '0',
    startDate: startDateInput,
    endDate: endDateInput
  });


  useEffect(() => {
    scrapeArXiv();
  }, [query]);

  const scrapeArXiv = async () => {
    const response = await fetch(`http://export.arxiv.org/api/query?search_query=all:"${query.topic}"&sortBy=submittedDate&sortOrder=descending&max_results=${query.limit}`);
    const responseString = await response.text();
    const results = (new window.DOMParser()).parseFromString(responseString, "text/xml").querySelectorAll('entry');
    var data = []

    for (var i = 0; i < results.length; i++) {
      var metadata = {}
      var csvRow = []

      metadata['title'] = results[i].querySelector('title').textContent.trim();
      const rawDate = results[i].querySelector('published').textContent.trim();
      metadata['published'] = new Date(rawDate).toDateString();

      const authors = results[i].querySelectorAll('author');
      var names = [];
      for (var j = 0; j < authors.length; j++) {
        names.push(authors[j].textContent.trim());
      }

      metadata['authors'] = names.join(', ');
      metadata['summary'] = results[i].querySelector('summary').textContent.trim();
      metadata['link'] = results[i].querySelectorAll('link')[1].getAttribute('href').trim();
      data.push(metadata);
    }

    console.log('Scraped ' + data.length + ' entries');
    setEntries(data);
    setShowResults(!showResults);
  }


  const updateTopic = e => {
    setTopicInput(e.target.value);
  }

  const updateLimit = e => {
    setLimitInput(e.target.value);
  }

  const updateStartDate = e => {
    setStartDateInput(e.target.value);
  }

  const updateEndDate = e => {
    setEndDateInput(e.target.value);
  }

  const clearInput = () => {
    setTopicInput('');
    setLimitInput('');
    setStartDateInput('');
    setEndDateInput('');
  }

  const isValidInput = () => {
    if (!topicInput.trim()) {
      alert("Please enter a topic");
      return false;
    }

    if (!limitInput.trim() || isNaN(limitInput.trim()) || parseInt(limitInput.trim(), 10) <= 0) {
      alert("Please enter a valid limit for the number of results (MAX 1000)");
      return false;
    }

    if (startDateInput.trim() || endDateInput.trim()) {
      if (!endDateInput.trim()) {
        alert("Please enter an end date range");
        return false;
      } else if (!startDateInput.trim()) {
        alert("Please enter a start date range");
        return false;
      } else if (new Date(startDateInput.trim()) == 'Invalid Date') {
        alert("Please enter a valid start date (Ex: 1/01/2019)");
        return false;
      } else if (new Date(endDateInput.trim()) == 'Invalid Date') {
        alert("Please enter a valid end date (Ex: 5/01/2020)");
        return false;
      } else if (new Date(startDateInput.trim()).getTime() >= new Date(endDateInput.trim()).getTime()) {
        alert("Make sure the start date range is earlier than the end date range");
        return false;
      }
    }

    return true;
  }

  const submitForm = e => {
    e.preventDefault();
    if (isValidInput()) {
      setShowResults(false);
      setQuery({
        topic: topicInput.trim(),
        limit: Math.min(1000, parseInt(limitInput.trim(), 10)),
        startDate: startDateInput.trim(),
        endDate: endDateInput.trim()
      });
    }
    clearInput();
  }

  return (
    <div className='App'>
      <Helmet>
        <title>{"arXived"}</title>
      </Helmet>
      <header className="App-header">
        <h1 className="App-title">arXived</h1>
      </header>

      <form onSubmit={submitForm} className="input-form" >

        <input className="topic-search-bar" type="text" placeholder="Enter a topic of interest"
          value={topicInput} onChange={updateTopic} />
        <input className="limit-input-bar" type="text" placeholder="Result Limit (MAX 1000)"
          value={limitInput} onChange={updateLimit} />
        <input className="date-input-bar" type="text" placeholder="1/01/2019 (Optional Start Range)"
          value={startDateInput} onChange={updateStartDate} />
        <input className="date-input-bar" type="text" placeholder="5/01/2020 (Optional End Range)"
          value={endDateInput} onChange={updateEndDate} />

        <button className="submit-button" type="submit">
          Scrape arXiv
        </button>
      </form>

      <div className='entries'>
        {showResults ? <div style={{ 'textAlign': 'center' }}>
                          <h1>Top matches for "{query.topic}"</h1>
                          <p><CSVLink data={entries} filename="arxiv_data.csv" target="_blank">Download as CSV</CSVLink> for the full results.</p>
                        </div> 
                        : null}
        {showResults ? entries.slice(0, Math.min(20, entries.length)).map((data, i) => (
                          <Entry key={i} data={data} />
                        )): null}

      </div>
    </div>
  );
};

export default App;
