import React, { useEffect, useState } from 'react';
import { Helmet } from 'react-helmet';
import Entry from './Entry.js';
import './App.css';

const App = () => {

  // ['title', 'published', 'firstauthor', 'lastauthor', 'summary', 'category', 'link']

  const [entries, setEntries] = useState([]);
  const [topicInput, setTopicInput] = useState('automated commentary');
  const [limitInput, setLimitInput] = useState(10);
  const [startDateInput, setStartDateInput] = useState('');
  const [endDateInput, setEndDateInput] = useState('');
  const [query, setQuery] = useState({
    topic: topicInput,
    limit: limitInput,
    startDate: startDateInput,
    endDate: endDateInput
  });


  useEffect(() => {
    scrapeArXiv();
  }, [query]);

  const scrapeArXiv = async () => {
    const response = await fetch(`http://export.arxiv.org/api/query?search_query=all:"${query.topic}"&sortBy=submittedDate&sortOrder=descending&max_results=${query.limit}`);
    const responseString = await response.text();
    const results = (new window.DOMParser()).parseFromString(responseString, "text/xml").querySelectorAll('entry');
    var data = []

    for (var i = 0; i < results.length; i++) {
      var metadata = {}
      metadata['title'] = results[i].querySelector('title').textContent.trim();
      metadata['published'] = results[i].querySelector('published').textContent.trim();
      metadata['summary'] = results[i].querySelector('summary').textContent.trim();
      metadata['link'] = results[i].querySelector('link')[1].getAttribute('href').trim();

      const authors = results[i].querySelectorAll('author');
      var names = [];
      for (var j = 0; j < authors.length; j++) {
        names.push(authors[j].textContent.trim());
      }
      metadata['authors'] = names;
      data.push(metadata);
    }

    console.log('Scraped ' + data.length + ' entries');
    setEntries(data);
  }


  const updateTopic = e => {
    setTopicInput(e.target.value);
  }

  const updateLimit = e => {
    setLimitInput(e.target.value);
  }

  const updateStartDate = e => {
    setStartDateInput(e.target.value);
  }

  const updateEndDate = e => {
    setEndDateInput(e.target.value);
  }

  const clearInput = () => {
    setTopicInput('');
    setLimitInput(10);
    setStartDateInput('');
    setEndDateInput('');
  }

  const submitForm = e => {
    e.preventDefault();
    setQuery({
      topic: topicInput,
      limit: limitInput,
      startDate: startDateInput,
      endDate: endDateInput
    });
    clearInput();
  }

  return (
    <div className='App'>
      <Helmet>
          <title>{"arXived"}</title>
      </Helmet>
      <header className="App-header">
        <h1 className="App-title">arXived</h1>
      </header>

      <form onSubmit={submitForm} className="input-form" >

        <input className="topic-search-bar" type="text" placeholder="Enter a topic of interest"
          value={topicInput} onChange={updateTopic} />

        <input className="date-input-bar" type="text" placeholder="1/01/2019 (Optional Start Range)"
          value={startDateInput} onChange={updateStartDate} />
        <input className="date-input-bar" type="text" placeholder="5/01/2020 (Optional End Range)"
          value={endDateInput} onChange={updateEndDate} />
        <input className="limit-input-bar" type="text" placeholder="10 (Max Results)"
          value={limitInput} onChange={updateLimit} />

        <button className="submit-button" type="submit">
          Scrape arXiv
        </button>

        <button className="submit-button" type="submit">
          Download CSV
        </button>
      </form>

      <div className='entries'>
        <div style={{'textAlign': 'center'}}>
          <h1>Top Matches for "{query.topic}"</h1>
          <p>Download as CSV for the full results.</p>
        </div>
        {entries.map((data, i) => (
          <Entry key={i} data={data} />
        ))}
      </div>
    </div>
  );
};

export default App;
