import React, {useEffect, useState} from 'react';
import './App.css';

const App = () => {


  const topic = "quantum computing";
  const limit = 10;

  // ['title', 'published', 'firstauthor', 'lastauthor', 'summary', 'category', 'link']

  const [entries, setEntries] = useState([]);

  const BASE_API_URL = `http://export.arxiv.org/api/query?search_query=all:"${topic}"&sortBy=submittedDate&sortOrder=descending&max_results=${limit}`;

  useEffect(() => {
    scrapeArXiv();
  }, []);

  const scrapeArXiv = async () => {
    const response = await fetch(BASE_API_URL);
    const responseString = await response.text();
    const results = (new window.DOMParser()).parseFromString(responseString, "text/xml").querySelectorAll('entry');
    var data = []

    for (var i=0; i < results.length; i++) {
      var metadata = {}
      metadata['title'] = results[i].querySelector('title').textContent.trim();
      metadata['published'] = results[i].querySelector('published').textContent.trim();
      metadata['summary'] = results[i].querySelector('summary').textContent.trim();
      metadata['link'] = results[i].querySelector('link').getAttribute('href').trim();

      const authors = results[i].querySelectorAll('author'); 
      metadata['firstauthor'] = authors[0].textContent.trim();
      metadata['lastauthor'] = authors[authors.length-1].textContent.trim();

      data.push(metadata);
    }  
    console.log(data[0].firstauthor.trim());
  }


  return (
    <div className='App'>
      <form className="input-form">
        <input className="topic-search-bar" type="text"/>
        <button className="submit-button" type="submit">
          Scrape arXiv
        </button>
      </form>
    </div>
  );
};

export default App;
